mapper.select:
select * from ( select A.*, rownum as RNUM from ( select AP.*, B.pos_name from apply AP join position B on AP.POS_NO = B.POS_NO where AP.status_flag=#{statusFlag}
${searchCondition} like '%' || #{searchKeyword} || '%'
order by apply_no desc )A )
where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}
