mapper.select:
select * from( select b.*, rownum as RNUM from (
select apply_No, mem_No, resume_No,pos_No,apply_Name,ref_NAME, (select resume_Title from resume r where r.resume_No=a.resume_No)resume_Title, (select resume_File from resume r where r.resume_No=a.resume_No)resume_file, (select resume_Upfileflag from resume r where r.resume_No=a.resume_No)resume_Upfileflag, (select com_Name from comInfo where com_code=(select com_Code from position p where p.pos_No=a.pos_No)) com_Name, (select pos_Name from position p where p.pos_No=a.pos_No ) pos_Name, (select jikgun_Name from jikgun where jikgun_Code=(select jikgun_code from position p where p.pos_No=a.pos_No ))jikgun_Name , status_Flag,apply_regdate from apply a order by apply_No desc )B
where apply_No like '%' || #{searchKeyword} || '%' or mem_No like '%' || #{searchKeyword} || '%' or apply_Name like '%' || #{searchKeyword} || '%' or resume_Title like '%' || #{searchKeyword} || '%' or pos_Name like '%' || #{searchKeyword} || '%' or jikgun_Name like '%' || #{searchKeyword} || '%' or com_Name like '%' || #{searchKeyword} || '%' or ref_Name like '%' || #{searchKeyword} || '%'
)
where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}
