mapper.select:
select* from ( select A.*,rownum as RNUM from ( select pos_No, (select com_Name from comInfo c where c.com_Code=p.com_Code)com_Name, (select jikgun_Name from jikgun j where p.jikgun_Code=j.jikgun_Code)jikgun_Name, pos_Min_Sal,pos_Max_Sal,end_Date,pos_Name,pos_Status,pos_Regdate from position p order by pos_No desc )A
where pos_No like '%' || #{searchKeyword} || '%' or com_Name like '%' || #{searchKeyword} || '%' or jikgun_Name like '%' || #{searchKeyword} || '%' or pos_Name like '%' || #{searchKeyword} || '%'
)
where RNUM>#{firstRecordIndex} and RNUM <=#{firstRecordIndex} + #{recordCountPerPage}
